(()=>{"use strict";const e=document.getElementById("searchForm"),t=document.getElementById("searchInput"),n=document.getElementById("searchBtn");e.addEventListener("submit",(e=>{e.preventDefault()})),n.addEventListener("click",(async()=>{try{if(""===t.value)return;const e=await async function(e){try{let t=await fetch(`http://api.weatherapi.com/v1/current.json?key=7aa0e87b3a094a37906165204233007&q=${e}&aqi=no`,{mode:"cors"});return await t.json()}catch(e){return alert("Error recieving data"),null}}(t.value);console.log(e);const n=await async function(e){const{location:t,current:n}=e;return{locationName:t.name,weather_condition:n.condition.text,celsius:n.temp_c,feel_celsius:n.feelslike_c,wind:n.wind_kph}}(e);!function(e){document.getElementById("searchResult").classList.add("active");const t=document.getElementById("cityName"),n=document.getElementById("temperature"),c=document.getElementById("feelsLike"),a=document.getElementById("weather_condition"),o=document.getElementById("wind");t.textContent=`${e.locationName}`,a.textContent=`${e.weather_condition}`,c.textContent=`Feels like: ${e.celsius} °C`,n.textContent=`${e.celsius} °C`,o.textContent=`Wind: ${e.wind} km/h`}(n)}catch(e){alert("Error fetching data")}}))})();